export default {
    get: () => ({
        sql: `
            SELECT *
            FROM user
        `,
        values: [],
    }),

    find: (id) => ({
        sql: `
            SELECT *
            FROM user
            WHERE id = ?
        `,
        values: [id],
    }),

    add: (email) => ({
        sql: `
            INSERT INTO user(email)
            VALUES (?)
        `,
        values: [email],
    }),

    update: (email, id) => ({
        sql: `
            UPDATE user
            SET email = ?
            WHERE id = ?
        `,
        values: [
            email,
            id,
        ],
    }),

    delete: (id) => ({
        sql: `
            DELETE
            FROM user
            WHERE id = ?
        `,
        values: [id],
    }),
}
